import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  CheckSquare, 
  Plus, 
  Search, 
  Filter, 
  SortAsc, 
  User, 
  LogOut,
  Trash2,
  Edit3,
  Check,
  X,
  Calendar,
  Clock,
  Flag,
  Tag,
  MoreVertical,
  Moon,
  Sun,
  Bell,
  Settings,
  BarChart3,
  Target,
  TrendingUp
} from 'lucide-react';
import { useAuth } from '../contexts/AuthContext';
import { todosAPI } from '../services/api';

const Dashboard = () => {
  const [todos, setTodos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [newTodo, setNewTodo] = useState({ 
    title: '', 
    description: '', 
    priority: 'medium',
    category: 'general',
    dueDate: ''
  });
  const [showNewTodoForm, setShowNewTodoForm] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filter, setFilter] = useState('all'); // all, completed, pending
  const [sortBy, setSortBy] = useState('created'); // created, priority, dueDate
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [editingTodo, setEditingTodo] = useState(null);

  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const priorities = [
    { value: 'low', label: 'Low', color: 'green', icon: Flag },
    { value: 'medium', label: 'Medium', color: 'yellow', icon: Flag },
    { value: 'high', label: 'High', color: 'red', icon: Flag }
  ];

  const categories = [
    { value: 'general', label: 'General', color: 'gray' },
    { value: 'work', label: 'Work', color: 'blue' },
    { value: 'personal', label: 'Personal', color: 'green' },
    { value: 'shopping', label: 'Shopping', color: 'purple' },
    { value: 'health', label: 'Health', color: 'red' }
  ];

  useEffect(() => {
    loadTodos();
  }, []);

  const loadTodos = async () => {
    try {
      const data = await todosAPI.getTodos();
      setTodos(data);
    } catch (error) {
      console.error('Failed to load todos:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleCreateTodo = async (e) => {
    e.preventDefault();
    if (!newTodo.title.trim()) return;

    try {
      const todo = await todosAPI.createTodo(newTodo);
      setTodos([todo, ...todos]);
      setNewTodo({ title: '', description: '' });
      setShowNewTodoForm(false);
    } catch (error) {
      console.error('Failed to create todo:', error);
    }
  };

  const handleToggleTodo = async (todoId, completed) => {
    try {
      const todo = todos.find(t => t.id === todoId);
      const updatedTodo = await todosAPI.updateTodo(todoId, {
        title: todo.title,
        description: todo.description,
        completed: !completed
      });
      setTodos(todos.map(t => t.id === todoId ? updatedTodo : t));
    } catch (error) {
      console.error('Failed to update todo:', error);
    }
  };

  const handleDeleteTodo = async (todoId) => {
    if (!window.confirm('Are you sure you want to delete this task?')) return;

    try {
      await todosAPI.deleteTodo(todoId);
      setTodos(todos.filter(t => t.id !== todoId));
    } catch (error) {
      console.error('Failed to delete todo:', error);
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const filteredTodos = todos.filter(todo => {
    const matchesSearch = todo.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         todo.description?.toLowerCase().includes(searchTerm.toLowerCase());

    if (filter === 'completed') return matchesSearch && todo.completed;
    if (filter === 'pending') return matchesSearch && !todo.completed;
    return matchesSearch;
  });

  const stats = {
    total: todos.length,
    completed: todos.filter(t => t.completed).length,
    pending: todos.filter(t => !t.completed).length
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <CheckSquare className="h-12 w-12 text-blue-600 mx-auto mb-4 animate-pulse" />
          <p className="text-gray-600">Loading your tasks...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="container mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <div className="flex items-center gap-2">
                <CheckSquare className="h-8 w-8 text-blue-600" />
                <span className="text-2xl font-bold text-gray-900">TaskMaster</span>
              </div>
              <nav className="hidden md:flex items-center gap-6">
                <a href="#" className="text-blue-600 font-medium">Dashboard</a>
                <a href="#" className="text-gray-600 hover:text-gray-900">Projects</a>
                <a href="#" className="text-gray-600 hover:text-gray-900">Tasks</a>
                <a href="#" className="text-gray-600 hover:text-gray-900">Team</a>
              </nav>
            </div>

            <div className="flex items-center gap-4">
              <div className="flex items-center gap-2 text-gray-600">
                <User className="h-4 w-4" />
                <span>{user?.username}</span>
              </div>
              <button
                onClick={handleLogout}
                className="btn btn-secondary"
              >
                <LogOut className="h-4 w-4" />
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      <div className="container mx-auto px-6 py-8">
        <div className="flex flex-col lg:flex-row gap-8">
          {/* Sidebar */}
          <aside className="lg:w-64">
            <div className="card mb-6">
              <h3 className="font-semibold text-gray-900 mb-4">Overview</h3>
              <div className="space-y-3">
                <div className="flex items-center justify-between">
                  <span className="text-gray-600">Total Tasks</span>
                  <span className="font-semibold">{stats.total}</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-gray-600">Completed</span>
                  <span className="font-semibold text-green-600">{stats.completed}</span>
                </div>
                <div className="flex items-center justify-between">
                  <span className="text-gray-600">Pending</span>
                  <span className="font-semibold text-orange-600">{stats.pending}</span>
                </div>
              </div>
            </div>

            <div className="card">
              <h3 className="font-semibold text-gray-900 mb-4">Quick Actions</h3>
              <button
                onClick={() => setShowNewTodoForm(true)}
                className="btn btn-primary w-full mb-3"
              >
                <Plus className="h-4 w-4" />
                New Task
              </button>
            </div>
          </aside>

          {/* Main Content */}
          <main className="flex-1">
            {/* Controls */}
            <div className="flex flex-col sm:flex-row gap-4 mb-6">
              <div className="flex-1 relative">
                <Search className="h-4 w-4 absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                <input
                  type="text"
                  placeholder="Search tasks..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="form-input pl-10"
                />
              </div>

              <div className="flex gap-2">
                <select
                  value={filter}
                  onChange={(e) => setFilter(e.target.value)}
                  className="form-input"
                >
                  <option value="all">All Tasks</option>
                  <option value="pending">Pending</option>
                  <option value="completed">Completed</option>
                </select>

                <button
                  onClick={() => setShowNewTodoForm(true)}
                  className="btn btn-primary"
                >
                  <Plus className="h-4 w-4" />
                  Add Task
                </button>
              </div>
            </div>

            {/* New Todo Form */}
            {showNewTodoForm && (
              <div className="card mb-6">
                <h3 className="font-semibold text-gray-900 mb-4">Create New Task</h3>
                <form onSubmit={handleCreateTodo}>
                  <div className="form-group">
                    <input
                      type="text"
                      placeholder="Task title"
                      value={newTodo.title}
                      onChange={(e) => setNewTodo({...newTodo, title: e.target.value})}
                      className="form-input"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <textarea
                      placeholder="Task description (optional)"
                      value={newTodo.description}
                      onChange={(e) => setNewTodo({...newTodo, description: e.target.value})}
                      className="form-input"
                      rows={3}
                    />
                  </div>
                  <div className="flex gap-2">
                    <button type="submit" className="btn btn-primary">
                      <Check className="h-4 w-4" />
                      Create Task
                    </button>
                    <button
                      type="button"
                      onClick={() => setShowNewTodoForm(false)}
                      className="btn btn-secondary"
                    >
                      <X className="h-4 w-4" />
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            )}

            {/* Tasks List */}
            <div className="space-y-4">
              {filteredTodos.length === 0 ? (
                <div className="card text-center py-12">
                  <CheckSquare className="h-12 w-12 text-gray-300 mx-auto mb-4" />
                  <h3 className="text-lg font-medium text-gray-900 mb-2">No tasks found</h3>
                  <p className="text-gray-600 mb-4">
                    {searchTerm ? 'Try adjusting your search terms.' : 'Create your first task to get started.'}
                  </p>
                  {!searchTerm && (
                    <button
                      onClick={() => setShowNewTodoForm(true)}
                      className="btn btn-primary"
                    >
                      <Plus className="h-4 w-4" />
                      Create Task
                    </button>
                  )}
                </div>
              ) : (
                filteredTodos.map((todo) => (
                  <div key={todo.id} className="card">
                    <div className="flex items-start gap-4">
                      <button
                        onClick={() => handleToggleTodo(todo.id, todo.completed)}
                        className={`mt-1 w-5 h-5 rounded border-2 flex items-center justify-center ${
                          todo.completed
                            ? 'bg-green-500 border-green-500 text-white'
                            : 'border-gray-300 hover:border-green-500'
                        }`}
                      >
                        {todo.completed && <Check className="h-3 w-3" />}
                      </button>

                      <div className="flex-1">
                        <h3 className={`font-medium ${
                          todo.completed ? 'text-gray-500 line-through' : 'text-gray-900'
                        }`}>
                          {todo.title}
                        </h3>
                        {todo.description && (
                          <p className={`text-sm mt-1 ${
                            todo.completed ? 'text-gray-400' : 'text-gray-600'
                          }`}>
                            {todo.description}
                          </p>
                        )}
                        <div className="flex items-center gap-4 mt-2">
                          <span className={`text-xs px-2 py-1 rounded-full ${
                            todo.completed
                              ? 'bg-green-100 text-green-800'
                              : 'bg-orange-100 text-orange-800'
                          }`}>
                            {todo.completed ? 'Completed' : 'In Progress'}
                          </span>
                        </div>
                      </div>

                      <div className="flex items-center gap-2">
                        <button
                          onClick={() => handleDeleteTodo(todo.id)}
                          className="p-2 text-gray-400 hover:text-red-600 rounded-lg hover:bg-red-50"
                        >
                          <Trash2 className="h-4 w-4" />
                        </button>
                      </div>
                    </div>
                  </div>
                ))
              )}
            </div>
          </main>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
